INSERT INTO wp__wsd_plugin_alerts VALUES("28","0","0","fix_wp_index_uploads","<strong>\"/wp-content/uploads\"</strong> directory is not secure from directory listing.","<p>A directory listing provides an attacker with the complete index of all the resources located inside of the directory.
                            The specific risks and consequences vary depending on which files are listed and accessible.
                            Therefore, it is important to protect your directories by having an empty index.php or index.htm file inside them.</p>","","2014-06-29 11:50:14","2014-06-29 11:48:46");
DROP TABLE IF EXISTS wp__wsd_plugin_live_traffic;CREATE TABLE `wp__wsd_plugin_live_traffic` (
  `entryId` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `entryTime` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `entryIp` text,
  `entryReferrer` text,
  `entryUA` text,
  `entryRequestedUrl` text,
  `entryCountry` varchar(125) NOT NULL,
  `entryCity` varchar(125) NOT NULL,
  `blogId` int(10) NOT NULL DEFAULT '1',
  PRIMARY KEY (`entryId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wp__wsd_plugin_scan;CREATE TABLE `wp__wsd_plugin_scan` (
  `entryId` bigint(20) NOT NULL AUTO_INCREMENT,
  `scanId` int(11) NOT NULL,
  `filePath` varchar(1000) NOT NULL,
  `dateModified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `fileNotFound` tinyint(4) NOT NULL DEFAULT '0',
  PRIMARY KEY (`entryId`),
  UNIQUE KEY `entryId_UNIQUE` (`entryId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wp__wsd_plugin_scans;CREATE TABLE `wp__wsd_plugin_scans` (
  `scanId` int(11) NOT NULL AUTO_INCREMENT,
  `scanStartDate` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `scanEndDate` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `scanResult` int(11) NOT NULL DEFAULT '0',
  `failReason` varchar(5000) NOT NULL DEFAULT '',
  `scanType` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`scanId`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wp_commentmeta;CREATE TABLE `wp_commentmeta` (
  `meta_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `comment_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  `meta_key` varchar(255) DEFAULT NULL,
  `meta_value` longtext,
  PRIMARY KEY (`meta_id`),
  KEY `comment_id` (`comment_id`),
  KEY `meta_key` (`meta_key`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS wp_comments;CREATE TABLE `wp_comments` (
  `comment_ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `comment_post_ID` bigint(20) unsigned NOT NULL DEFAULT '0',
  `comment_author` tinytext NOT NULL,
  `comment_author_email` varchar(100) NOT NULL DEFAULT '',
  `comment_author_url` varchar(200) NOT NULL DEFAULT '',
  `comment_author_IP` varchar(100) NOT NULL DEFAULT '',
  `comment_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `comment_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `comment_content` text NOT NULL,
  `comment_karma` int(11) NOT NULL DEFAULT '0',
  `comment_approved` varchar(20) NOT NULL DEFAULT '1',
  `comment_agent` varchar(255) NOT NULL DEFAULT '',
  `comment_type` varchar(20) NOT NULL DEFAULT '',
  `comment_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `user_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`comment_ID`),
  KEY `comment_post_ID` (`comment_post_ID`),
  KEY `comment_approved_date_gmt` (`comment_approved`,`comment_date_gmt`),
  KEY `comment_date_gmt` (`comment_date_gmt`),
  KEY `comment_parent` (`comment_parent`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;
